                  </Typography>
                </Grid>
                <Grid item xs={12} sm={4}>
                  <Card className={classes.paper}>
                    <Button href="confirmed-cases">
                      <Typography
                        variant="h5"
                        component="h2"
                        style={{
                          textAlign: "center",
                          color: "black",
                          fontWeight: 600
                        }}
                      >
                        Confirmed Cases
                      </Typography>
                    </Button>
                    <Typography
                      variant="h5"
                      component="h2"
                      style={{
                        textAlign: "center",
                        color: "blue",
                        fontWeight: 600
                      }}
                    >
                      {confirmed ? confirmed.value : "0"}
                    </Typography>
                  </Card>
                </Grid>
                <Grid item xs={12} sm={4}>
                  <Card className={classes.paper}>
                    <Button href="recovered-cases">
                      <Typography
                        variant="h5"
                        component="h2"
                        style={{
                          textAlign: "center",
                          color: "black",
                          fontWeight: 600
                        }}
                      >
                        Recovered Cases
                      </Typography>
                    </Button>
                    <Typography
                      variant="h5"
                      component="h2"
                      style={{
                        textAlign: "center",
                        color: "green",
                        fontWeight: 600
                      }}
