                </Grid>
              </Grid>
            </Paper> */}
            <div
              style={{
                marginTop: "10%",
                display: "flex",
                flexDirection: "row",
                justifyContent: "space-between",
                alignItems: "center"
              }}
            >
              <Paper
                style={{
                  width: "30%",
                  height: "80px",
                  textAlign: "center",
                  backgroundColor: "#0080FF",
                  color: "white",
                  padding: "1%"
                }}
              >
                <span style={{ fontWeight: 550, fontSize: "20px" }}>
                  {confirmed ? confirmed.value : "0"}
                </span>
                <br />
                <span style={{ fontWeight: 550, fontSize: "20px" }}>
                  Confirmed Case
                </span>
              </Paper>
              <Paper
                style={{
                  width: "30%",
                  height: "80px",
                  textAlign: "center",
                  backgroundColor: "#00A572",
                  color: "white",
                  padding: "1%"
                }}
              >
                <span style={{ fontWeight: 550, fontSize: "20px" }}>
                  {recovered ? recovered.value : "0"}
                </span>
                <br />
                <span style={{ fontWeight: 550, fontSize: "20px" }}>
                  Recovered Case
                </span>
              </Paper>
              <Paper
                style={{
                  width: "30%",
