              </Paper>
            </div>
            <Card
              style={{
                marginTop: "3%"
              }}
            >
              <div style={{ marginTop: "1%", marginBottom: "3%",marginLeft:'1%' }}>
                <Typography
                  variant="subtitle1"
                  component="h2"
                  style={{
                    color: "black",
                    fontWeight: 550
                  }}
                >
                  DATA INFORMATION
                </Typography>
                <Typography
                  variant="body2"
                  component="span"
                  style={{
                    color: "black",
                    fontWeight: 500
                  }}
                >
                  Important Details of Cases of NCOV19 ( Coronavirus disease )
                </Typography>
                <br />
                <span>
                  Click Here for{" "}
                  <Link
                    component="button"
                    variant="body2"
                    color="primary"
                    onClick={() => props.history.push("/daily-update")}
                  >
                    Daily Update
                  </Link>
                </span>
              </div>
              <div style={{ padding: "1%" }}>
                <TableData data={list} />
              </div>
            </Card>
          </Container>
        </div>
      )}
    </React.Fragment>
  );
}
