import React, { useEffect, useReducer } from "react";
import { DailyUpdate } from "../../actions/Api"

import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TablePagination from "@material-ui/core/TablePagination";
import TableRow from "@material-ui/core/TableRow";

const useStyles = makeStyles({
  root: {
    width: "100%"
  },
  container: {
    maxHeight: 440
  }
});

function reducer(state, action) {
  switch (action.type) {
    case "load": {
      return {
        ...state,
        isloading: true
      };
    }
    case "loaded": {
      return {
        ...state
      };
    }
    default:
      break;
  }
  return state;
}

const InitialState = {
  isloading: false,
  rows: [],
  columns: [
    { id: "countryRegion", label: "Name", minWidth: 170 },
    { id: "iso3", label: "ISO\u00a0Code", minWidth: 100 },
    {
      id: "provinceState",
      label: "Province State",
      minWidth: 170,
      align: "right",
      format: value => value.toLocaleString()
    },
    {
      id: "confirmed",
      label: "Confirmed Case",
      minWidth: 170,
      align: "right",
      format: value => value.toFixed(2)
    },
    {
      id: "recovered",
      label: "Recovered Case",
      minWidth: 170,
      align: "right",
      format: value => value.toFixed(2)
    },
    {
      id: "deaths",
      label: "Deaths Case",
      minWidth: 170,
      align: "right",
      format: value => value.toFixed(2)
    }
  ]
};

export default function TableData(props) {
  const classes = useStyles();
  const [state, dispatch] = useReducer(reducer, InitialState);
  const { rows, columns, isloading } = state;

  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = event => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  useEffect(() => {
    let isSubscribed = true;
    if (isSubscribed) {
      dispatch({ type: "load" });
      const Loaddata = async () => {
        await DailyUpdate()
          .then(res => {
            console.log(res.data);
            // dispatch({
            //   type: "loaded",
            //   name: "general_info",
            // });
          })
          .catch(err => console.log(err));

      };
      Loaddata();
    }
    return () => {
      console.log("unmounted");
      isSubscribed = false;
    };
  }, []);

  return (
    <Paper className={classes.root}>
      <TableContainer className={classes.container}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {columns.map(column => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth, fontWeight: 600 }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {props.data
              .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
              .map((row, index) => {
                return (
                  <TableRow hover role="checkbox" tabIndex={-1} key={index}>
                    {columns.map((column, indx) => {
                      const value = row[column.id];
                      return (
                        <TableCell key={indx} align={column.align}>
                          {value}
                        </TableCell>
                      );
                    })}
                  </TableRow>
                );
              })}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[10, 25, 100, 200]}
        component="div"
        count={props.data.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
      />
    </Paper>
  );
}
