import React, { useReducer } from "react";
import { CountryData } from "../../actions/Api";

import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TablePagination from "@material-ui/core/TablePagination";
import TableRow from "@material-ui/core/TableRow";
import TextField from "@material-ui/core/TextField";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles(theme => ({
  root: {
    width: "98%",
    margin: "1% auto"
  },
  container: {
    maxHeight: 600
  },
  formControl: {
    marginLeft: "1%",
    minWidth: 250
  }
}));

function reducer(state, action) {
  switch (action.type) {
    case "loaded": {
      return {
        ...state,
        isloading: false,
        rows: action.item,
        rowsbackup: action.item
      };
    }
    case "search": {
      return {
        ...state,
        isloading: false,
        rows: action.item
      };
    }
    default:
      break;
  }
  return state;
}

const InitialState = {
  rows: [],
  rowsbackup: []
};

export default function CountriesData(props) {
  const classes = useStyles();
  const [state, dispatch] = useReducer(reducer, InitialState);
  const { rows, rowsbackup } = state;

  const onSearch = async e => {
    e.preventDefault();
    console.log(e)
    const result = rows.filter(element =>
      element.combinedKey
        .toLowerCase()
        .match(e.currentTarget.value.toLowerCase())
    );
    if (result.length > 0 && e.currentTarget.value.length > 0) {
      dispatch({
        type: "search",
        name: "general_info",
        item: result
      });
    } else {
      dispatch({
        type: "search",
        name: "general_info",
        item: rowsbackup
      });
    }
  };
  return (
    <React.Fragment>
      <Paper className={classes.root}>
        <div style={{ display: "flex", paddingLeft: "1%", paddingRight: "1%" }}>
          <form className={classes.root} noValidate autoComplete="off" onSubmit={e=>onSearch(e)}>
            <TextField
              id="filled-required"
              variant="filled"
              placeholder="Search Name..."
              fullWidth
            //   onChange={e => onSearch(e)}
            />
          </form>
        </div>
      </Paper>
    </React.Fragment>
  );
}
