import React, { useEffect, useReducer } from "react";
import { General_Info } from "../../actions/Api";
import { CircleLoading } from "react-loadingg";

import CssBaseline from "@material-ui/core/CssBaseline";
import Container from "@material-ui/core/Container";
import { makeStyles } from "@material-ui/core/styles";
import Typography from '@material-ui/core/Typography';
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Card from '@material-ui/core/Card';

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
    marginTop: "5%"
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary
  }
}));

function reducer(state, action) {
  switch (action.type) {
    case "load": {
      return {
        ...state,
        isloading: true
      };
    }
    case "loaded": {
        console.log(action)
      return {
        ...state,
        isloading: false,
        data: action.data,
        confirmed: action.confirmed
      };
    }
    default:
      break;
  }
  return state;
}
const InitialState = {
  isloading: false,
  data: {},
  confirmed: null,
};
export default function Tablelist() {
  const classes = useStyles();
  const [state, dispatch] = useReducer(reducer, InitialState);
  const { data,confirmed, isloading } = state;
  useEffect(() => {
    let isSubscribed = true;
    if (isSubscribed) {
      dispatch({ type: "load" });
      const Loaddata = async () => {
        await General_Info()
          .then(res => {
            console.log(res.data);
            dispatch({
              type: "loaded",
              name: "general_info",
              data: res.data,
              confirmed:res.data.confirmed
            });
          })
          .catch(err => console.log(err));
      };
      Loaddata();
    }
    return () => {
      console.log("unmounted");
      isSubscribed = false;
    };
  }, []);
  console.log(isloading ? 'loading' : confirmed);
  return (
    <React.Fragment>
      {isloading ? (
        <CircleLoading />
      ) : (
        <div>
          <CssBaseline />
          <Container maxWidth="lg">
            <Paper className={classes.root}>
              <Grid container spacing={3}>
                <Grid item xs={12}>
                  <Typography variant="h4" component="h2" style={{textAlign:'center', fontWeight:600, padding:'3%'}}>
                    Novel Coronavirus General Information
                  </Typography>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <div className={classes.paper}>
                  <Typography variant="h5" component="h2" style={{textAlign:'center', color:'black',fontWeight:600, padding:'3%'}}>
                    Confirmed Cases
                  </Typography>
                  <Typography variant="h5" component="h2" style={{textAlign:'center', color:'black',fontWeight:600, padding:'3%'}}>
                    {confirmed.useDebugValue(value)}
                  </Typography>
                  </div>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <Paper className={classes.paper}>xs=12 sm=6</Paper>
                </Grid>
                <Grid item xs={6} sm={3}>
                  <Paper className={classes.paper}>xs=6 sm=3</Paper>
                </Grid>
                <Grid item xs={6} sm={3}>
                  <Paper className={classes.paper}>xs=6 sm=3</Paper>
                </Grid>
                <Grid item xs={6} sm={3}>
                  <Paper className={classes.paper}>xs=6 sm=3</Paper>
                </Grid>
                <Grid item xs={6} sm={3}>
                  <Paper className={classes.paper}>xs=6 sm=3</Paper>
                </Grid>
              </Grid>
            </Paper>
          </Container>
        </div>
      )}
    </React.Fragment>
  );
}
