import React, { useReducer } from "react";
import { CountryData } from "../../actions/Api";

import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TablePagination from "@material-ui/core/TablePagination";
import TableRow from "@material-ui/core/TableRow";
import TextField from "@material-ui/core/TextField";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles(theme => ({
  root: {
    width: "100%",
    margin: "1% auto"
  },
  container: {
    maxHeight: 600
  },
  formControl: {
    marginLeft: "1%",
    minWidth: 250
  }
}));

function reducer(state, action) {
  switch (action.type) {
    case "loaded": {
      return {
        ...state,
        isloading: false,
        rows: action.item,
        rowsbackup: action.item
      };
    }
    case "search": {
      return {
        ...state,
        isloading: false,
        searhval: action.item
      };
    }
    case "error": {
        return {
          ...state,
          iserror: true,
        };
      }
    default:
      break;
  }
  return state;
}

const InitialState = {
  rows: [],
  searhval: "",
  iserror: false,
  rowsbackup: []
};

export default function CountriesData(props) {
  const classes = useStyles();
  const [state, dispatch] = useReducer(reducer, InitialState);
  const { searhval, iserror } = state;

  const onSearch = async e => {
    e.preventDefault();
    if (searhval) {
      const Loaddata = async () => {
        await CountryData(searhval)
          .then(res => {
            console.log(res.data);
          })
          .catch(err => console.log(err.response.data.error.message));
      };
      Loaddata();
    } else {
        dispatch({type:'error'})
    }
  };
  return (
    <React.Fragment>
      <Paper className={classes.root}>
        <div style={{ display: "flex", paddingLeft: "1%", paddingRight: "1%" }}>
          <form
            className={classes.root}
            noValidate
            autoComplete="off"
            onSubmit={e => onSearch(e)}
          >
            <TextField
              id="standard-search"
              placeholder="Search Country ... e.g.Philippines"
              fullWidth
              onChange={e =>
                dispatch({
                  type: "search",
                  name: "general_info",
                  item: e.target.value
                })
              }
            />
          </form>
        </div>
        {iserror ? (
          <Typography
            variant="h6"
            component="h2"
            style={{
              color: "black",
              padding: "1%",
              fontWeight: 500
            }}
          >
            Error
          </Typography>
        ) : (
          <div>
            <Typography
              variant="h6"
              component="h2"
              style={{
                color: "black",
                padding: "1%",
                fontWeight: 500
              }}
            >
              Confirmed Cases:
            </Typography>

            <Typography
              variant="h6"
              component="h2"
              style={{
                color: "black",
                padding: "1%",
                fontWeight: 500
              }}
            >
              Recovered:
            </Typography>

            <Typography
              variant="h6"
              component="h2"
              style={{
                color: "black",
                padding: "1%",
                fontWeight: 500
              }}
            >
              Deaths:
            </Typography>
          </div>
        )}
      </Paper>
    </React.Fragment>
  );
}
