import React, { useEffect, useReducer } from "react";
import { General_Info, Confirmed_Cases } from "../../actions/Api";
import { CircleLoading } from "react-loadingg";
import TableData from "./tabledata";

import CssBaseline from "@material-ui/core/CssBaseline";
import Container from "@material-ui/core/Container";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import Button from "@material-ui/core/Button";

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
    marginTop: "5%"
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary
  }
}));

function reducer(state, action) {
  switch (action.type) {
    case "load": {
      return {
        ...state,
        isloading: true
      };
    }
    case "loaded": {
      return {
        ...state,
        isloading: false,
        confirmed: action.confirmed,
        recovered: action.recovered,
        deaths: action.deaths
      };
    }
    case "loaded": {
      return {
        ...state,
        isloading: false,
        list: action.item
      };
    }
    default:
      break;
  }
  return state;
}
const InitialState = {
  isloading: false,
  confirmed: null,
  recovered: null,
  deaths: null,
  list: []
};
export default function Tablelist() {
  const classes = useStyles();
  const [state, dispatch] = useReducer(reducer, InitialState);
  const { list, confirmed, recovered, deaths, isloading } = state;
  useEffect(() => {
    let isSubscribed = true;
    if (isSubscribed) {
      dispatch({ type: "load" });
      const Loaddata = async () => {
        await General_Info()
          .then(res => {
            console.log(res.data);
            dispatch({
              type: "loaded",
              name: "general_info",
              confirmed: res.data.confirmed,
              recovered: res.data.recovered,
              deaths: res.data.deaths
            });
          })
          .catch(err => console.log(err));

        await Confirmed_Cases()
          .then(res => {
            // console.log(res.data);
            dispatch({
              type: "loaded2",
              name: "confirmed_cases",
              item: res.data
            });
          })
          .catch(err => console.log(err));
      };

      Loaddata();
      const Loaddata2 = async () => {

        await Confirmed_Cases()
          .then(res => {
            // console.log(res.data);
            dispatch({
              type: "loaded2",
              name: "confirmed_cases",
              item: res.data
            });
          })
          .catch(err => console.log(err));
      };
      Loaddata2();
    }
    return () => {
      console.log("unmounted");
      isSubscribed = false;
    };
  }, []);
  return (
    <React.Fragment>
      {isloading ? (
        <CircleLoading />
      ) : (
        <div>
          <CssBaseline />
          <Container maxWidth="lg">
            <Paper className={classes.root}>
              <Grid container spacing={3}>
                <Grid item xs={12}>
                  <Typography
                    variant="h4"
                    component="h2"
                    style={{
                      textAlign: "center",
                      fontWeight: 600,
                      padding: "3%"
                    }}
                  >
                    Novel Coronavirus General Information
                  </Typography>
                </Grid>
                <Grid item xs={12} sm={4}>
                  <Card className={classes.paper}>
                    <Button href="confirmed-cases">
                      <Typography
                        variant="h5"
                        component="h2"
                        style={{
                          textAlign: "center",
                          color: "black",
                          fontWeight: 600
                        }}
                      >
                        Confirmed Cases
                      </Typography>
                    </Button>
                    <Typography
                      variant="h5"
                      component="h2"
                      style={{
                        textAlign: "center",
                        color: "blue",
                        fontWeight: 600
                      }}
                    >
                      {confirmed ? confirmed.value : "0"}
                    </Typography>
                  </Card>
                </Grid>
                <Grid item xs={12} sm={4}>
                  <Card className={classes.paper}>
                    <Button href="recovered-cases">
                      <Typography
                        variant="h5"
                        component="h2"
                        style={{
                          textAlign: "center",
                          color: "black",
                          fontWeight: 600
                        }}
                      >
                        Recovered Cases
                      </Typography>
                    </Button>
                    <Typography
                      variant="h5"
                      component="h2"
                      style={{
                        textAlign: "center",
                        color: "green",
                        fontWeight: 600
                      }}
                    >
                      {recovered ? recovered.value : "0"}
                    </Typography>
                  </Card>
                </Grid>

                <Grid item xs={12} sm={4}>
                  <Card className={classes.paper}>
                    <Button href="death-cases">
                      <Typography
                        variant="h5"
                        component="h2"
                        style={{
                          textAlign: "center",
                          color: "black",
                          fontWeight: 600
                        }}
                      >
                        Deaths Cases
                      </Typography>
                    </Button>
                    <Typography
                      variant="h5"
                      component="h2"
                      style={{
                        textAlign: "center",
                        color: "red",
                        fontWeight: 600
                      }}
                    >
                      {deaths ? deaths.value : "0"}
                    </Typography>
                  </Card>
                </Grid>

                <Grid item xs={6} sm={3}>
                  <Card className={classes.paper}>
                    <Typography
                      variant="subtitle1"
                      component="h2"
                      style={{
                        textAlign: "center",
                        color: "blue",
                        fontWeight: 550
                      }}
                    >
                      Daily Update
                    </Typography>
                  </Card>
                </Grid>
                <Grid item xs={6} sm={3}>
                  <Paper className={classes.paper}>xs=6 sm=3</Paper>
                </Grid>
                <Grid item xs={6} sm={3}>
                  <Paper className={classes.paper}>xs=6 sm=3</Paper>
                </Grid>
                <Grid item xs={6} sm={3}>
                  <Paper className={classes.paper}>xs=6 sm=3</Paper>
                </Grid>
              </Grid>
            </Paper>
            <div
              style={{
                marginTop: "10%",
                display: "flex",
                flexDirection: "row",
                justifyContent: "space-between",
                alignItems: "center"
              }}
            >
              <Paper
                style={{
                  width: "30%",
                  height: "80px",
                  textAlign: "center",
                  backgroundColor: "#0080FF",
                  color: "white",
                  padding: "1%"
                }}
              >
                <span style={{ fontWeight: 550, fontSize: "20px" }}>
                  {confirmed ? confirmed.value : "0"}
                </span>
                <br />
                <span style={{ fontWeight: 550, fontSize: "20px" }}>
                  Confirmed Case
                </span>
              </Paper>
              <Paper
                style={{
                  width: "30%",
                  height: "80px",
                  textAlign: "center",
                  backgroundColor: "#00A572",
                  color: "white",
                  padding: "1%"
                }}
              >
                <span style={{ fontWeight: 550, fontSize: "20px" }}>
                  {recovered ? recovered.value : "0"}
                </span>
                <br />
                <span style={{ fontWeight: 550, fontSize: "20px" }}>
                  Recovered Case
                </span>
              </Paper>
              <Paper
                style={{
                  width: "30%",
                  height: "80px",
                  textAlign: "center",
                  backgroundColor: "#D21F3C",
                  color: "white",
                  padding: "1%"
                }}
              >
                <span style={{ fontWeight: 550, fontSize: "20px" }}>
                  {deaths ? deaths.value : "0"}
                </span>
                <br />
                <span style={{ fontWeight: 550, fontSize: "20px" }}>
                  Deaths Case
                </span>
              </Paper>
            </div>
            <Card
              style={{
                marginTop: "3%"
              }}
            >
              <TableData data={list}/>
            </Card>
          </Container>
        </div>
      )}
    </React.Fragment>
  );
}
