import React, { useReducer } from "react";
import { CountryData } from "../../actions/Api";

import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TablePagination from "@material-ui/core/TablePagination";
import TableRow from "@material-ui/core/TableRow";
import TextField from "@material-ui/core/TextField";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles(theme => ({
  root: {
    width: "100%",
    margin: "1% auto"
  },
  container: {
    maxHeight: 600
  },
  formControl: {
    marginLeft: "1%",
    minWidth: 250
  }
}));

function reducer(state, action) {
  switch (action.type) {
    case "loaded": {
      return {
        ...state,
        iserror: false,
        confirmed: action.confirmed,
        recovered: action.recovered,
        deaths: action.deaths,
        country: action.country
      };
    }
    case "search": {
      return {
        ...state,
        isloading: false,
        searhval: action.item
      };
    }
    case "error": {
      return {
        ...state,
        iserror: true,
        erroval: "",
        confirmed: "",
        recovered: "",
        deaths: ""
      };
    }
    case "error-fetch": {
      return {
        ...state,
        iserror: false,
        erroval: action.payloads,
        confirmed: "",
        recovered: "",
        deaths: ""
      };
    }
    default:
      break;
  }
  return state;
}

const InitialState = {
  searhval: "",
  iserror: false,
  erroval: "",
  confirmed: "",
  recovered: "",
  deaths: "",
  country: ""
};

export default function CountriesData(props) {
  const classes = useStyles();
  const [state, dispatch] = useReducer(reducer, InitialState);
  const { searhval, iserror, erroval, confirmed, recovered, deaths, country } = state;

  const onSearch = async e => {
    e.preventDefault();
    if (searhval) {
      const Loaddata = async () => {
        await CountryData(searhval)
          .then(res => {
            console.log(res.data);
            dispatch({
              type: "loaded",
              confirmed: res.data.confirmed.value,
              recovered: res.data.recovered.value,
              deaths: res.data.deaths.value,
              country: searhval
            });
          })
          .catch(err =>
            dispatch({
              type: "error-fetch",
              payloads: err.response.data.error.message
            })
          );
      };
      Loaddata();
    } else {
      console.log(true);
      dispatch({ type: "error" });
    }
  };
  return (
    <React.Fragment>
      <Paper className={classes.root}>
        <div style={{ display: "flex", paddingLeft: "1%", paddingRight: "1%" }}>
          <form
            className={classes.root}
            noValidate
            autoComplete="off"
            onSubmit={e => onSearch(e)}
          >
            <TextField
              id="standard-search"
              placeholder="Search Country ... e.g.Philippines"
              fullWidth
              onChange={e =>
                dispatch({
                  type: "search",
                  name: "general_info",
                  item: e.target.value
                })
              }
            />
          </form>
        </div>
        {iserror ? (
          <Typography
            variant="h6"
            component="h2"
            style={{
              color: "red",
              padding: "1%",
              fontWeight: 500
            }}
          >
            {erroval ? erroval : "Invalid Null Value"}
          </Typography>
        ) : (
          <Typography
            variant="h6"
            component="h2"
            style={{
              color: "red",
              padding: "1%",
              fontWeight: 500
            }}
          >
            {erroval}
          </Typography>
        )}
        <Typography
          variant="h6"
          component="h2"
          style={{
            color: "black",
            padding: "1%",
            fontWeight: 500
          }}
        >
          Country: {country}
        </Typography>

        <Typography
          variant="h6"
          component="h2"
          style={{
            color: "black",
            padding: "1%",
            fontWeight: 500
          }}
        >
          Confirmed Cases:{" "}
          <span style={{ color: "#0080FF", fontWeight: 550 }}>{confirmed}</span>
        </Typography>

        <Typography
          variant="h6"
          component="h2"
          style={{
            color: "black",
            padding: "1%",
            fontWeight: 500
          }}
        >
          Recovered:{" "}
          <span style={{ color: "#00A572", fontWeight: 550 }}>{recovered}</span>
        </Typography>

        <Typography
          variant="h6"
          component="h2"
          style={{
            color: "black",
            padding: "1%",
            fontWeight: 500
          }}
        >
          Deaths:{" "}
          <span style={{ color: "#D21F3C", fontWeight: 550 }}>{deaths}</span>
        </Typography>
      </Paper>
    </React.Fragment>
  );
}
