import React, { useEffect, useReducer } from "react";
import { General_Info } from "../../actions/Api";
import { CircleLoading } from 'react-loadingg';

function reducer(state, action) {
  switch (action.type) {
    case "load": {
      return {
        ...state,
        isloading: true
      };
    }
    case "loaded": {
        return {
          ...state,
          isloading: false,
          data: action.data
        };
      }
    default:
      break;
  }
  return state;
}
const InitialState = {
  isloading: false,
  data: {}
};
export default function Tablelist() {
  const [state, dispatch] = useReducer(reducer, InitialState);
  const { data, isloading } = state;
  useEffect(() => {
    let isSubscribed = true;
    if (isSubscribed) {
        dispatch({type:'load'})
        const Loaddata = async () =>{
            await General_Info()
            .then(res =>{
                console.log(res.data)
                dispatch({
                    type: "loaded",
                    name: "general_info",
                    data: res.data,
                  });
            })
            .catch(err=>console.log(err))
        }
        Loaddata();
    }
    return () => {
      console.log("unmounted");
      isSubscribed = false;
    };
  }, []);
  console.log(state)
  return (
    <React.Fragment>
        {isloading? <CircleLoading />:
        (
            <h1>Table</h1>
        )}

    </React.Fragment>
  );
}
